CREATE SCHEMA opportunity;

CREATE TABLE opportunity.tbl_opportunity (
    id bigint NOT NULL,
    buyer_unit character varying(100) NOT NULL,
    auction_number character varying(100) NOT NULL,
    account character varying(50) NOT NULL,
    portal character varying(100) NOT NULL,
    items_odin jsonb NOT NULL,
    answers json DEFAULT '[]'::json NOT NULL,
    created_at timestamp with time zone DEFAULT now() NOT NULL,
    publish_date timestamp with time zone NOT NULL,
    update_at timestamp with time zone,
    auction_properties_odin jsonb NOT NULL,
    declined boolean DEFAULT false NOT NULL,
    auction_mode character varying(100) NOT NULL,
    auction_type character varying(100) DEFAULT 'open'::character varying,
    auction_description text NOT NULL,
    target_proposal timestamp with time zone NOT NULL,
    target_operational timestamp with time zone,
    target_client timestamp with time zone,
    auction_date timestamp with time zone NOT NULL,
    observatrion text,
    initial_target_proposal timestamp(0) with time zone NOT NULL
);

ALTER TABLE opportunity.tbl_opportunity ALTER COLUMN id ADD GENERATED ALWAYS AS IDENTITY (
    SEQUENCE NAME opportunity.tbl_opportunity_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);


CREATE TABLE opportunity.tbl_reject_proposals_items (
